mapper.select:
SELECT SUM( temp1.`quantity` * temp1.costPrice ) AS totalCost FROM (SELECT temp.*, `product`.`productCode` AS proproductcode, `product`.`costPrice` FROM (SELECT `alipay_order`.`orderCode` AS aliordercode, `alipay_order`.`occuredTime`, `order_detail`.orderStatus AS detailorderstatus, `order_detail`.`orderCode` AS detailordercode, `order_detail`.`quantity`, `order_detail`.`productCode` AS detailproductcode FROM `alipay_order` LEFT OUTER JOIN `order_detail` ON `alipay_order`.`orderCode` = `order_detail`.`orderCode` UNION SELECT `alipay_order`.`orderCode` AS aliordercode, `alipay_order`.`occuredTime`, `order_detail`.orderStatus AS detailorderstatus, `order_detail`.`orderCode` AS detailordercode, `order_detail`.`quantity`, `order_detail`.`productCode` AS detailproductcode FROM `alipay_order` RIGHT OUTER JOIN `order_detail` ON `alipay_order`.`orderCode` = `order_detail`.`orderCode`) AS temp LEFT OUTER JOIN `product` ON temp.detailproductcode LIKE CONCAT(`product`.`productCode`,'%') ) AS temp1
WHERE aliordercode IS NOT NULL AND detailorderstatus != '交易关闭' AND occuredTime IS NOT NULL AND detailordercode IS NOT NULL AND quantity IS NOT NULL AND detailproductcode IS NOT NULL AND proproductcode IS NOT NULL AND costprice IS NOT NULL AND occuredTime LIKE CONCAT(#{chooseMonth}, '%')
AND proproductcode = #{orderCode}
